import React from 'react';
import './Header.Style.scss';
import { Link } from 'react-router-dom';
import { ReactComponent as Logo } from '../../Assets/crown.svg';
import { auth } from '../../Firebase/Firebase.Utils';
import { connect } from 'react-redux';
import CartIcon from '../CartIcon/CartIcon.Component';
import  CartDropDown  from '../CartDropdown/CartDropDown.Component';
import {createStructuredSelector} from 'reselect';
import {currentUserSelector} from '../../Redux/User/User.Selector';
import {hiddenSelector} from '../../Redux/Cart/Cart.Selector';
import {HeaderContainer, LogoContainer,OptionsContainer,DivOption,LinkOption} from './Header.Style';


const Header = ({ currentUser,hidden }) => (

    <HeaderContainer>
        <LogoContainer to="/">
            <Logo className="logo" />
        </LogoContainer>
        <OptionsContainer>
            <LinkOption className="option" to="/shop"> SHOP </LinkOption>
            <LinkOption className="option" to="/shop"> CONTACT </LinkOption>
            { currentUser?
                <DivOption onClick={() => auth.signOut() }>SIGN OUT</DivOption>
                :
                <DivOption to="/signin">SIGN IN</DivOption>
            }
            <CartIcon />
        </OptionsContainer>
        {
            !hidden? <CartDropDown /> : ''
        }
        
    </HeaderContainer>
);

const mapStateToProps = createStructuredSelector({
    currentUser: currentUserSelector,
    hidden: hiddenSelector
});

export default connect(mapStateToProps)(Header);
