import { all, call} from 'redux-saga/effects';
import { userSaga } from '../Redux/User/User.Saga';

import { UpdateCollectionsDataSaga } from '../Redux/Collection/Collection.Sagas';

export default function* RootSaga(){
    yield all([
            call(UpdateCollectionsDataSaga),
            call(userSaga)
        ])
}
