import React from 'react'
import './SignIn.Style.scss'
import FormInput from '../FormInput/FormInput.Component'
import CustomButton from '../CustomButton/CustomButton.Component'
import { auth } from '../../Firebase/Firebase.Utils';
import { googleSignInStart } from '../../Redux/User/User.Action';
import { connect } from 'react-redux';

class SignIn extends React.Component{

    constructor(){
        super();
        this.state = {
            email:'',
            password:''
        }
    }

    handleSubmit = async event =>{
        event.preventDefault();

        const {email, password} = this.state;

        try {
            await auth.signInWithEmailAndPassword(email, password);
            this.setState({email:'',password:''});
        } catch (error) {
            console.log(error);
        } 

        this.setState({email:'', password:''});
    }

    handleChange = event => {
        const { value, name } = event.target;

        this.setState({[name] : value});
    }

    render(){
        const {googleSignInStartSaga} = this.props;
        return(
            <div className='sign-in'>
                <h2 className='title'>I already have an account</h2>
                <span>Sign in with your email and password</span>
                <form onSubmit={this.handleSubmit}>
                    <FormInput handleChange={this.handleChange} label="Email" type="email" name="email" value={this.state.email} />

                    <FormInput handleChange={this.handleChange} label="Password" type="password" name="password" value={this.state.password} />
                    <div className='buttons'>
                        <CustomButton type="submit">SIGN IN</CustomButton>
                        <CustomButton type="button" isSignInWithGoogle onClick={googleSignInStartSaga} >
                            Google Sign In
                        </CustomButton>
                    </div>
                </form>
            </div>    
        );
    }
}

const mapDispatchToProps = dispatch => ({
    googleSignInStart:  () => dispatch(googleSignInStart())
})
export default connect(null, mapDispatchToProps)(SignIn);
