import {takeLatest,call,put,all} from 'redux-saga/effects';
import { userActionTypes }  from './User.Types';
import { googleProvider,auth,createUserProfileDocument } from '../../Firebase/Firebase.Utils';
import { googleSignInFailed, googleSignInSuccess,
         emailPassWordSignInFailed,emailPassWordSignInSuccess } from './User.Action';


export function* onGoogleSignInStart(){
    yield takeLatest(userActionTypes.LOGIN_WITH_GOOGLE_START,signInWithGoogle);
}

export function* signInWithGoogle(){
    try {

        const { user } = yield auth.signInWithPopup(googleProvider);
        const userRef = yield call(createUserProfileDocument,user);
        const userSnapShopt = yield userRef.get();

        yield put(googleSignInSuccess({id: userSnapShopt.id,...userSnapShopt.data()}))

    } catch (error) {
       yield put(googleSignInFailed(error));
    }
}

export function* onEmailPasswordSignInStart(){
    yield takeLatest(userActionTypes.LOGIN_WITH_EMAIL_PASSWORD_START,emailPasswordSignIn);
}

export function* emailPasswordSignIn(){
    try {

        const { user } = yield auth.signInWithPopup(googleProvider);
        const userRef = yield call(createUserProfileDocument,user);
        const userSnapShopt = yield userRef.get();

        yield put(googleSignInSuccess({id: userSnapShopt.id,...userSnapShopt.data()}))

    } catch (error) {
       yield put(googleSignInFailed(error));
    }
}

export function* userSaga(){
    yield all([call(onGoogleSignInStart)]);
}
