import { userActionTypes } from '../User/User.Types'

const INITIAL_STATE = {
    currentUser : null,
    error:undefined
}

const UserReducer = (state  = INITIAL_STATE, action) =>{
    switch(action.type){
        case userActionTypes.SET_CURRENT_USER:
            return{
                ...state,
                currentUser: action.payload
            }
        case userActionTypes.LOGIN_WITH_GOOGLE_SUCCESS:
        case userActionTypes.LOGIN_WITH_EMAIL_PASSWORD_SUCCESS:
            return{
                ...state,
                currentUser:action.payload,
                error:null
            }
        case userActionTypes.LOGIN_WITH_GOOGLE_SUCCESS:
        case userActionTypes.LOGIN_WITH_EMAIL_PASSWORD_SUCCESS:
            return{
                ...state,
                error:action.payload
            }
        default:
            return state;
    }
}

export default UserReducer;
