import {takeLatest,call,put,all} from 'redux-saga/effects';
import { userActionTypes }  from './User.Types';
import { googleProvider,auth } from '../../Firebase/Firebase.Utils';
import { googleSignInFailed } from './User.Action';


export function* onGoogleSignInStartSaga(){
    takeLatest(userActionTypes.LOGIN_WITH_EMAIL_PASSWORD_START,googleSignInSaga);
}

export function* googleSignInSaga(){
    try {

        const userRef = yield auth.signInWithPopup(googleProvider);
        console.log(userRef);

    } catch (error) {
       
    }
}


export function* userSaga(){
    yield all([call(googleSignInStartSaga)]);
}
