import React from 'react';
import CollectionOverview from '../../Components/CollectionOverview/CollectionOverview.Component';
import {Route} from 'react-router-dom';
import CollectionPage from '../CollectionPage/CollectionPage.Component';
import {UpdateCollectionsData} from '../../Redux/Collection/Collection.Action';
import {connect} from 'react-redux';
import WithSpinner from '../../Components/WithSpinner/WithSpinner.Component';
import {isFectchingSelector,isFectchingSuccessSelector} from '../../Redux/Collection/Collection.Selector';
import {createStructuredSelector} from 'reselect';

const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);
const CollectionPageSpinner = WithSpinner(CollectionPage);

class Shop extends React.Component{ 

    componentDidMount(){
        const {UpdateCollectionsData} = this.props;
        UpdateCollectionsData(); 
    }

    render(){
        const {match,isFectching} = this.props;
        return(
            <div className='shop-page'> 
                <Route exact path={`${match.path}`}   render={ (props) => <CollectionOverviewWithSpinner isLoading={isFectching}  {...props}  /> } />
                <Route path={`${match.path}/:collectionId`} render={ (props) => <CollectionPageSpinner isLoading={isFectching} {...props} /> } />
            </div>
        );
    }
}     

const mapDispatchToProps = dispatch => ({
    UpdateCollectionsData: collections => 
    dispatch(UpdateCollectionsData(collections))
})

const mapStateToProps = createStructuredSelector({
    isFectching: isFectchingSelector,
    isFectchingDone: isFectchingSuccessSelector
});
export default connect(mapStateToProps,mapDispatchToProps)(Shop)
