import { all, call} from 'redux-saga/effects';
import { onGoogleSignInStartSaga } from './User/User.Sagas';

import { UpdateCollectionsDataSaga } from './Collection/Collection.Sagas';

export default function* RootSaga(){
    yield all([
            call(UpdateCollectionsDataSaga),
            call(onGoogleSignInStartSaga)
        ])
}
